package fr.hokanosekai;import fr.hokanosekai.commands.Login;import fr.hokanosekai.commands.Register;import fr.hokanosekai.listeners.player.OnJoinListener;import fr.hokanosekai.listeners.player.OnMoveListener;import fr.hokanosekai.managers.DbManager;import fr.hokanosekai.managers.LoginManager;import org.bukkit.Bukkit;import org.bukkit.plugin.PluginManager;import org.bukkit.plugin.java.JavaPlugin;public class Main extends JavaPlugin {    private static Main INSTANCE;    private DbManager dbManager;    private LoginManager loginManager;    @Override    public void onEnable() {        INSTANCE = this;        PluginManager pluginManager = this.getServer().getPluginManager();        dbManager = new DbManager();        loginManager = new LoginManager(this);        pluginManager.registerEvents(new OnJoinListener(this), this);        pluginManager.registerEvents(new OnMoveListener(this), this);        this.getCommand("Login").setExecutor(new Login(this));        this.getCommand("Register").setExecutor(new Register(this));    }    @Override    public void onDisable() {        this.dbManager.close();    }    public static Main getINSTANCE() {        return INSTANCE;    }    public DbManager getDbManager() {        return dbManager;    }    public LoginManager getLoginManager() {        return loginManager;    }}