package fr.hokanosekai.db;public class DbCredentials {    private String dbHost;    private String dbUser;    private String dbPasswd;    private String dbName;    private String dbPort;    public DbCredentials(String dbHost, String dbUser, String dbPasswd, String dbName, String dbPort) {        this.dbHost = dbHost;        this.dbUser = dbUser;        this.dbPasswd = dbPasswd;        this.dbName = dbName;        this.dbPort = dbPort;    }    public String toURI() {        final StringBuilder sb = new StringBuilder();        sb.append("jdbc:mysql://")                .append(dbHost)                .append(":")                .append(dbPort)                .append("/")                .append(dbName)                .append("?characterEncoding=utf8&useSSL=false&useUnicode=true");        return sb.toString();    }    public String getDbUser() {        return dbUser;    }    public String getDbPasswd() {        return dbPasswd;    }}