package fr.hokanosekai.listeners.player;import fr.hokanosekai.Main;import org.bukkit.ChatColor;import org.bukkit.Location;import org.bukkit.configuration.file.FileConfiguration;import org.bukkit.entity.Player;import org.bukkit.event.EventHandler;import org.bukkit.event.Listener;import org.bukkit.event.player.PlayerMoveEvent;import java.util.UUID;public class OnMoveListener implements Listener {    Main main;    FileConfiguration config;    public OnMoveListener(Main main){        this.main = main;        config = main.getConfiguration();    }    @EventHandler    public void onMove(PlayerMoveEvent e){        final UUID uuid = e.getPlayer().getUniqueId();        final Player player = e.getPlayer();        if (!main.getLoginManager().isLogged(uuid)){            double xF = e.getFrom().getX();            double zF = e.getFrom().getZ();            double yF = e.getFrom().getY();            Location location = new Location(player.getWorld(), xF, yF, zF);            e.setTo(location);            e.getPlayer().sendMessage(ChatColor.translateAlternateColorCodes('&', config.getString("msg.move")));        }    }}