package fr.hokanosekai.listeners.player;import fr.hokanosekai.Main;import org.bukkit.Location;import org.bukkit.entity.Player;import org.bukkit.event.EventHandler;import org.bukkit.event.Listener;import org.bukkit.event.player.PlayerMoveEvent;import java.util.UUID;public class OnMoveListener implements Listener {    private Main main;    public OnMoveListener(Main main){        this.main = main;    }    @EventHandler    public void onMove(PlayerMoveEvent e){        final UUID uuid = e.getPlayer().getUniqueId();        final Player player = e.getPlayer();        if (!main.getLoginManager().isLogged(uuid)){            double xF = e.getFrom().getX();            double zF = e.getFrom().getZ();            double yF = e.getFrom().getY();            Location location = new Location(player.getWorld(), xF, yF, zF);            e.setTo(location);            e.getPlayer().sendMessage("Â§cPlease register or login");        }    }}