package fr.hokanosekai.managers;import fr.hokanosekai.Main;import fr.hokanosekai.db.DbConnection;import fr.hokanosekai.db.DbCredentials;import org.bukkit.configuration.file.FileConfiguration;import java.sql.SQLException;public class DbManager {    Main main;    FileConfiguration configuration;    private DbConnection loginConnection;    public DbManager(Main main){        this.main = main;        configuration = main.getConfiguration();        this.loginConnection = new DbConnection(new DbCredentials(                configuration.getString("db.host"),                configuration.getString("db.username"),                configuration.getString("db.password"),                configuration.getString("db.name"),                configuration.getString("db.port")        ));    }    public DbConnection getLoginConnection() {        return loginConnection;    }    public void close(){        try {            this.loginConnection.close();        } catch (SQLException throwables) {            throwables.printStackTrace();        }    }}