package fr.hokanosekai.managers;import fr.hokanosekai.db.DbConnection;import fr.hokanosekai.db.DbCredentials;import java.sql.SQLException;public class DbManager {    private DbConnection loginConnection;    public DbManager(){        this.loginConnection = new DbConnection(new DbCredentials("localhost","root","","plugin-mc","3306"));    }    public DbConnection getLoginConnection() {        return loginConnection;    }    public void close(){        try {            this.loginConnection.close();        } catch (SQLException throwables) {            throwables.printStackTrace();        }    }}