package fr.hokanosekai.managers;import fr.hokanosekai.Main;import fr.hokanosekai.db.DbConnection;import fr.hokanosekai.db.DbCredentials;import org.bukkit.configuration.file.FileConfiguration;import java.sql.SQLException;public class DbManager {    Main main;    FileConfiguration config = main.getFileConfiguration();    private DbConnection loginConnection;    public DbManager(Main main){        this.main = main;        this.loginConnection = new DbConnection(new DbCredentials(                config.getString("db.host"),                config.getString("db.username"),                config.getString("db.password"),                config.getString("db.name"),                config.getString("db.port")        ));    }    public DbConnection getLoginConnection() {        return loginConnection;    }    public void close(){        try {            this.loginConnection.close();        } catch (SQLException throwables) {            throwables.printStackTrace();        }    }}