package fr.hokanosekai.managers;import fr.hokanosekai.Main;import java.util.HashMap;import java.util.UUID;public class LoginManager {    Main main;    private HashMap<UUID, String> playerLogged;    public LoginManager(Main main){        this.main = main;        this.playerLogged = new HashMap<>();    }    public HashMap<UUID, String> getPlayerLogged() {        return playerLogged;    }    public boolean isLogged(UUID uuid){        return playerLogged.containsKey(uuid);    }}