package fr.hokanosekai.utils;import fr.hokanosekai.Main;import org.bukkit.configuration.file.FileConfiguration;import org.bukkit.configuration.file.YamlConfiguration;import java.io.File;import java.io.IOException;public class FileUtils {    Main main;    public FileUtils(Main main){        this.main = main;    }    public void createFile(String fileName){        if (!main.getDataFolder().exists()){            main.getDataFolder().mkdir();        }        File file = new File(main.getDataFolder(), fileName +".yml");        if (!file.exists()){            try {                file.createNewFile();                setDefault(getConfig("Config"), file);            } catch (IOException exception) {                exception.printStackTrace();            }        }    }    public File getFile(String fileName){        return new File(main.getDataFolder(), fileName+".yml");    }    public FileConfiguration getConfig(String fileName){        return YamlConfiguration.loadConfiguration(getFile(fileName));    }    public void save(File file, FileConfiguration config){        try {            config.save(file);        } catch (IOException exception) {            exception.printStackTrace();        }    }    public void setDefault(FileConfiguration configuration, File file){        configuration.set("db.host", "localhost");        configuration.set("db.username", "plugin");        configuration.set("db.password", "roh64kjcdDBbQL7z");        configuration.set("db.name", "plugin-mc");        configuration.set("db.port", 3306);        save(file, configuration);    }}